function fallback_module() {
    return {
        _: {
            './wrapper': { $: '' }
        },
        api: fallback_instance
    }

    function fallback_instance() {
        return {
            _: {
                './wrapper': { $: '' }
            },
            drive: {
                'entries/': {},
                'theme/': {}
            }
        }
    }
}